# --- GET DATA ---
end_date = date.today()
start_date = end_date - timedelta(days=days_to_pull)

df = yf.download(
    tickers,
    period='3mo',
    group_by="ticker",
    auto_adjust=False
)

# --- TRANSFORM ---
records = []

for ticker in tickers:
    ticker_df = df[ticker].reset_index()
    ticker_df["Symbol"] = ticker
    records.append(ticker_df)

final_df = pd.concat(records)

final_df = final_df.rename(columns={
    "Date": "date",
    "Open": "open",
    "High": "high",
    "Low": "low",
    "Close": "close",
    "Volume": "volume"
})

final_df = final_df[["Symbol", "date", "open", "high", "low", "close", "volume"]]
data = final_df.to_dict(orient="records")

conn = psycopg2.connect(
    host=os.environ['SUPABASE_HOST'],
    database=os.environ['SUPABASE_DATABASE'],
    user=os.environ['SUPABASE_USER'],
    password=os.environ['SUPABASE_PASSWORD'],
    port=5432
)

cur = conn.cursor()

for row in data:
    cur.execute("""
        INSERT INTO stg_stock_prices
        (symbol, date, open, high, low, close, volume, load_timestamp)
        VALUES (%s,%s,%s,%s,%s,%s,%s, NOW())
        ON CONFLICT (symbol, date) DO NOTHING
    """, (
        row["Symbol"],
        row["date"],
        row["open"],
        row["high"],
        row["low"],
        row["close"],
        row["volume"]
    ))

conn.commit()
cur.close()
conn.close()
